package easy.Task_459;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class SolutionTest {
    Solution solution =new Solution();

    String s1 = "abab";

    String s2 = "aba";

    String s3 = "abcabcabcabc";

    String s4 = "abaababaab";

    String s5 = "abaababaababaababaab";

    String s6 = "abac";

    String s7 = "ababab";

    String s8 = "babbabbabbabbab";

    String s9 = "";

    @Test
    void repeatedSubstringPattern1() {
        assertTrue(solution.repeatedSubstringPattern(s1));
    }

    @Test
    void repeatedSubstringPattern2() {
        assertFalse(solution.repeatedSubstringPattern(s2));
    }

    @Test
    void repeatedSubstringPattern3() {
        assertTrue(solution.repeatedSubstringPattern(s3));
    }

    @Test
    void repeatedSubstringPattern4() {
        assertTrue(solution.repeatedSubstringPattern(s4));
    }

    @Test
    void repeatedSubstringPattern5() {
        assertTrue(solution.repeatedSubstringPattern(s5));
    }

    @Test
    void repeatedSubstringPattern6() {
        assertFalse(solution.repeatedSubstringPattern(s6));
    }

    @Test
    void repeatedSubstringPattern7() {
        assertTrue(solution.repeatedSubstringPattern(s7));
    }

    @Test
    void repeatedSubstringPattern8() {
        assertTrue(solution.repeatedSubstringPattern(s8));
    }


    @Test
    void repeatedSubstringPattern9() {
        assertFalse(solution.repeatedSubstringPattern(s9));
    }
}